TestAssertion.expression:
let $messages :=
  (for $record in $db
     let $typesFound := $record//*[local-name()='MD_ScopeDescription' and namespace-uri()='http://www.isotc211.org/2005/gmd']
     for $typeFound in $typesFound
       let $countElements := count($typeFound/*)
       let $countChoice0 := count($typeFound/*[local-name()='features' and namespace-uri()='http://www.isotc211.org/2005/gmd'])
       let $countChoice1 := count($typeFound/*[local-name()='dataset' and namespace-uri()='http://www.isotc211.org/2005/gmd'])
       let $countChoice2 := count($typeFound/*[local-name()='featureInstances' and namespace-uri()='http://www.isotc211.org/2005/gmd'])
       let $countChoice3 := count($typeFound/*[local-name()='attributeInstances' and namespace-uri()='http://www.isotc211.org/2005/gmd'])
       return
       if ($countElements gt 1) then
         local:addMessage('TR.moreThanOneOptionError', map { 'localName': 'More content', 'namespace': 'namespace2'  })
       else if (($countChoice0 + $countChoice1 + $countChoice2 + $countChoice3) lt 1) then 
         local:addMessage('TR.noValidOptionError', map { 'localName': 'propertyName1', 'namespace': 'namespace3'  })
       else if (($countChoice0 + $countChoice1 + $countChoice2 + $countChoice3) gt 1) then 
         local:addMessage('TR.moreThanOneValidOptionError', map { 'localName': 'propertyName2', 'namespace': 'namespace4'  })
       else ()
  )[position() le $limitErrors]
return
(if ($messages) then 'FAILED' else 'PASSED',
 local:error-statistics('TR.recordsWithErrors', count(fn:distinct-values($messages//etf:argument[@token='id']/text()))),
 $messages)
